import React, { useState } from 'react';
import Footer from "./components/Footer";
import 'react-responsive-carousel/lib/styles/carousel.min.css';
import image1 from './images/pexels-emrecan-2079249.jpg';
import image2 from './images/pexels-saviesa-home-1098995-2089698.jpg';
import image3 from './images/pexels-christa-grover-977018-2121120.jpg'; // Import the image
import image4 from './images/pexels-jessica-bryant-592135-1370704.jpg'
import HouseSidingRoundedIcon from '@mui/icons-material/HouseSidingRounded';
import './App.css'

function Details() {
  // State variables for search query and filter options
  const [searchQuery, setSearchQuery] = useState('');
  const [filterOption, setFilterOption] = useState('');

  // Function to handle changes in the search query
  const handleSearchChange = (e) => {
    setSearchQuery(e.target.value);
  };

  // Function to handle changes in the filter option
  const handleFilterChange = (e) => {
    setFilterOption(e.target.value);
  };

  // Render filtered content based on search query and filter option
  const pageContent = [
    { id: 1, title: 'Content 1', description: 'Description of Content 1' },
    { id: 2, title: 'Content 2', description: 'Description of Content 2' },
    { id: 3, title: 'Content 3', description: 'Description of Content 3' },
    // Add more content as needed
  ];

  // Filtered content based on search query and filter option
  const filteredContent = pageContent.filter(item => {
    // Filtering logic based on search query and filter option
    // For now, let's assume filtering is based on title and description
    return (
      item.title.toLowerCase().includes(searchQuery.toLowerCase()) &&
      (filterOption === '' || item.description.toLowerCase().includes(filterOption.toLowerCase()))
    );
  });

  return (
    <div className='home-pg'>
      <nav className="p-4">
        <div className="container mx-auto flex justify-between items-center">
          {/* Left side: Logo */}
          <div className="flex items-center">
            <HouseSidingRoundedIcon />
          </div>

          {/* Middle: Navigation Links */}
          <div className="flex justify-center">
            <span className="text-black font-bold">
              <span className="border-b-2 border-orange-500">Home</span>
            </span>
            <span className="text-black mx-4">Services</span>
            <span className="text-black mx-4">Pricing</span>
            <span className="text-black mx-4">Contact</span>
          </div>

          {/* Right side: Sign Up */}
          <div>
            <button className="text-black font-bold border-2 border-orange-500 px-4 py-2 rounded-full">
              Sign Up
            </button>
          </div>
        </div>
      </nav>

      {/* Search bar and filter option */}
      <div className='search-container'>
        <input
          type="text"
          placeholder="Search..."
          value={searchQuery}
          onChange={handleSearchChange}
          className="search-bar"
        />
        <select
          value={filterOption}
          onChange={handleFilterChange}
          className="filter-dropdown"
        >
          <option value="">All</option>
          {/* Add your filter options here */}
        </select>
      </div>

      {/* Display filtered content */}
      <div className='src'>
        {/* Render filtered content here */}
      </div>

      <Footer/>
    </div>
  );
}

export default Details;
