// src/reducers/houseReducer.js
const initialState = {
    keyword: '',
    filters: {}
  }
  
  const houseReducer = (state = initialState, action) => {
    switch(action.type) {
      case 'SEARCH_HOUSE':
        return {
          ...state,
          keyword: action.payload
        }
      case 'FILTER_HOUSE':
        return {
          ...state,
          filters: action.payload
        }
      default:
        return state
    }
  }
  
  export default houseReducer;
